import React, { useState } from "react";
import classNames from "classnames";
import { ProductProps } from "./Product.types";
import { ProductStatus } from "./Product.enum";
import { Rate } from "antd";
import { StarOutlined, StarFilled } from "@ant-design/icons";
import "antd/dist/antd.css";
import "./Product.scss";

const { available, unavailable, badge } = ProductStatus;

export const Product = ({ product }: ProductProps) => {
  const [loaded, setLoaded] = useState(false);
  const { name, description, rating, image, active, promo } = product;

  const productClassess = classNames("product", {
    "product--disactive": !active,
    "product--show": loaded,
  });

  // const imageClassess = classNames("product__image", {
  //   "product__image--active": loaded,
  // });

  const renderBadge = promo ? (
    <div className="product__badge">{badge}</div>
  ) : null;

  return (
    <>
      {loaded ? (
        <div className={productClassess}>
          {renderBadge}

          <img
            className="product__image"
            src={image}
            alt={name}
            onLoad={() => setLoaded(true)}
          />
          <div className="product__container">
            <h2 className="product__name">{name}</h2>
            <p className="product__description">{description}</p>
            <div className="product__ratings">
              <Rate
                disabled
                defaultValue={rating}
                character={({ index }: { index: number }) =>
                  rating < index + 1 ? <StarOutlined /> : <StarFilled />
                }
              />
            </div>
            <button className="product__button">
              {active ? available : unavailable}
            </button>
          </div>
        </div>
      ) : null}
    </>
  );
};
