import React from "react";
import "antd/dist/antd.css";
import { Checkbox } from "antd";
import { FiltersOptions } from "./Filters.enum";
import { FiltersProps } from "./Filters.types";

import "./Filters.scss";

const { active, promo } = FiltersOptions;

export const Filters = ({ setFilters }: FiltersProps) => {
  const handleChange = (filter: string) => setFilters(filter);
  return (
    <div className="filters">
      <Checkbox onChange={handleChange(active)}>{active}</Checkbox>
      <Checkbox onChange={handleChange(promo)}>{promo}</Checkbox>
    </div>
  );
};
