import React, { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "antd/dist/antd.css";
import { List, Pagination } from "antd";
import { AppRoute } from "routing/AppRoute.enum";
import { AppContext } from "../../providers/AppProviders";
import { Product } from "../product/Product";
import "./Products.scss";
import { Header } from "app/header/Header";

function itemRender(current: any, type: string, originalElement: any) {
  if (type === "prev") {
    // eslint-disable-next-line
    return <a>Previous</a>;
  }
  if (type === "next") {
    // eslint-disable-next-line
    return <a>Next</a>;
  }
  return originalElement;
}

export const Products = () => {
  const products = useContext(AppContext);
  const [filters, setFilters] = useState<string[]>(["all"]);
  const [currentPage, setCurrentPage] = useState(1);
  const [showProducts, setShowProducts] = useState(products);

  useEffect(() => setShowProducts(products), [products]);

  const filteredProducts = showProducts.filter((product) => {
    if (product["active"] && filters.includes("active")) {
      return product;
    }
    if (product["promo"] && filters.includes("promo")) {
      return product;
    }
    if (filters.includes("all")) {
      return product;
    }
  });

  const pageProducts = filteredProducts.slice(
    currentPage === 1 ? 0 : (currentPage - 1) * 4,
    4 * currentPage
  );

  // const renderProducts = () => {
  //   return (
  //     <List
  //       grid={{
  //         gutter: 24,
  //         xs: 1,
  //         sm: 1,
  //         md: 2,
  //         lg: 2,
  //         xl: 4,
  //         xxl: 4,
  //       }}
  //       pagination={{
  //         onChange: (page) => {
  //           setCurrentPage(page);
  //         },
  //         pageSize: 4,
  //         itemRender: itemRender,
  //         defaultPageSize: showProducts.length,
  //       }}
  //       dataSource={filteredProducts}
  //       renderItem={(item) => (
  //         <List.Item>
  //           <Product product={item} />
  //         </List.Item>
  //       )}
  //     />

  //   );
  // };

  const renderProducts = () => {
    if (!pageProducts) return null;

    return pageProducts.map((product) => {
      const { id } = product;
      return <Product product={product} key={id} />;
    });
  };

  return (
    <>
      <div className="products">
        <Header setFilters={setFilters} filters={filters} />
        <div className="products__container">{renderProducts()}</div>
        <Pagination
          defaultPageSize={4}
          total={filteredProducts.length}
          itemRender={itemRender}
          onChange={(page) => {
            setCurrentPage(page);
          }}
        />
      </div>

      <Link to={AppRoute.login}> Login </Link>
    </>
  );
};
