import React from "react";
import "antd/dist/antd.css";
import { Checkbox } from "antd";
import { FiltersOptions } from "./Filters.enum";

import "./Filters.scss";

const { active, promo } = FiltersOptions;

export const Filters = ({ setFilters }) => {
  const handleChange = (e: { target: { name: string } }) =>
    setFilters(e.target.name);
  return (
    <div className="filters">
      <Checkbox onChange={handleChange} name={active}>
        {active}
      </Checkbox>
      <Checkbox onChange={handleChange} name={promo}>
        {promo}
      </Checkbox>
    </div>
  );
};
