import React from "react";
import "antd/dist/antd.css";
import { Checkbox } from "antd";
import { FiltersOptions } from "./Filters.enum";
import { FiltersProps } from "./Filters.types";

import "./Filters.scss";
import { CheckboxChangeEvent } from "antd/lib/checkbox";

const { active, promo } = FiltersOptions;

export const Filters = ({ setFilters, filters }: FiltersProps) => {
  const handleChange = (e: CheckboxChangeEvent) =>
    setFilters([...filters, String(e.target.name)]);

  return (
    <div className="filters">
      <Checkbox name={active} onChange={handleChange}>
        {active}
      </Checkbox>
      <Checkbox name={promo} onChange={handleChange}>
        {promo}
      </Checkbox>
    </div>
  );
};
