import React, { useContext, useEffect, useState } from "react";
import classNames from "classnames";
import { Pagination } from "antd";
import { AppContext } from "../../providers/AppProviders";
import { Header } from "app/header/Header";
import { Product } from "../product/Product";
import { Info } from "../info/Info";
import "antd/dist/antd.css";
import "./Products.scss";

export const Products = () => {
  const products = useContext(AppContext);
  const [filters, setFilters] = useState<string[]>(["all"]);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [showProducts, setShowProducts] = useState(products);
  const [search, setSearch] = useState<string>("");

  useEffect(() => setShowProducts(products), [products]);

  const filteredProducts = showProducts!
    .filter((product) => {
      if (!product) return null;
      if (search === "") {
        return product;
      } else if (
        product.name.toLowerCase().includes(search.toLocaleLowerCase())
      ) {
        return product;
      }
    })
    .filter((product) => {
      if (product["active"] && filters.includes("active")) {
        return product;
      }
      if (product["promo"] && filters.includes("promo")) {
        return product;
      }
      if (filters.includes("all")) {
        return product;
      }
    });

  const countProducts = filteredProducts.length;
  const lastPage = Number((countProducts / 4).toFixed());

  const pageProducts = filteredProducts.slice(
    currentPage === 1 ? 0 : (currentPage - 1) * 8,
    8 * currentPage
  );

  const buttonPrevClassess = classNames("products__button", {
    "products__button--disabled": currentPage === 1,
  });

  const buttonNextClassess = classNames("products__button", {
    "products__button--disabled": currentPage === countProducts,
  });

  const isProduct = pageProducts.length !== 0;

  const renderProducts = () =>
    isProduct ? (
      <div className="products__container">
        {pageProducts.map((product) => {
          const { id } = product;
          return <Product product={product} key={id} />;
        })}
      </div>
    ) : (
      <Info />
    );

  const renderPagination = () =>
    isProduct ? (
      <div className="products__pagination">
        <button
          className={buttonPrevClassess}
          onClick={() => setCurrentPage(1)}
        >
          First
        </button>
        <Pagination
          current={currentPage}
          defaultPageSize={8}
          total={countProducts}
          onChange={(page) => {
            setCurrentPage(page);
          }}
        />
        <button
          className={buttonNextClassess}
          onClick={() => setCurrentPage(lastPage)}
        >
          Last
        </button>
      </div>
    ) : null;

  return (
    <>
      <div className="products">
        <Header
          setFilters={setFilters}
          filters={filters}
          setSearch={setSearch}
          setCurrentPage={setCurrentPage}
        />
        {renderProducts()}
        {renderPagination()}
      </div>
    </>
  );
};
