export const Products = () => {
  const products = useContext<AppContextProps | null>(AppContext);
  console.log(products);
  const [filters, setFilters] = useState<string[]>([all]);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [showProducts, setShowProducts] = useState<AppContextProps | null>(
    products
  );
  const [search, setSearch] = useState<string>("");

  useEffect(() => setShowProducts(products), [products]);

  const filteredProducts: ProductProps[] = mockProducts!
    .filter((product) => {
      if (!product) return null;
      if (search === "") {
        return product;
      } else if (
        product.name.toLowerCase().includes(search.toLocaleLowerCase())
      ) {
        return product;
      }
    })
    .filter((product) => {
      if (product.active && filters.includes(active)) {
        return product;
      }
      if (product.promo && filters.includes(promo)) {
        return product;
      }
      if (filters.includes(all)) {
        return product;
      }
    });

  const countProducts: number = filteredProducts.length;
  const lastPage: number = Math.ceil(countProducts / PRODUCTS_PER_PAGE);

  const pageProducts: ProductProps[] = filteredProducts.slice(
    currentPage === 1 ? 0 : (currentPage - 1) * PRODUCTS_PER_PAGE,
    PRODUCTS_PER_PAGE * currentPage
  );

  const buttonPrevClassess: string = classNames("products__button", {
    "products__button--disabled": currentPage === 1,
  });

  const buttonNextClassess: string = classNames("products__button", {
    "products__button--disabled": currentPage === lastPage,
  });

  const isProduct: boolean = pageProducts.length !== 0;

  const renderProducts: () => JSX.Element = () =>
    isProduct ? (
      <div className="products__container">
        {pageProducts.map((product) => {
          const { id, name, description, rating, image, active, promo } =
            product;
          return (
            <Product
              name={name}
              description={description}
              rating={rating}
              image={image}
              active={active}
              promo={promo}
              id={id}
              key={id}
            />
          );
        })}
      </div>
    ) : (
      <Info />
    );

  const renderPagination: () => JSX.Element | null = () => (
    <div className="products__pagination">
      <button className={buttonPrevClassess} onClick={() => setCurrentPage(1)}>
        First
      </button>
      {/* <Pagination
          current={currentPage}
          defaultPageSize={PRODUCTS_PER_PAGE}
          total={countProducts}
          onChange={(page) => {
            setCurrentPage(page);
          }}
        /> */}
      <Pagination
        count={lastPage}
        page={currentPage}
        siblingCount={lastPage > 6 ? -1 : 1}
        boundaryCount={currentPage > 2 ? 3 : 2}
        showFirstButton
        showLastButton
        hidePrevButton
        hideNextButton
        onChange={(event, value) => {
          setCurrentPage(value);
        }}
      />
      <button
        className={buttonNextClassess}
        onClick={() => setCurrentPage(lastPage)}
      >
        Last
      </button>
    </div>
  );

  return (
    <>
      <div className="products">
        <Header
          setFilters={setFilters}
          filters={filters}
          setSearch={setSearch}
          setCurrentPage={setCurrentPage}
        />
        {renderProducts()}
        {renderPagination()}
      </div>
    </>
  );
};
